-- ============================================
-- 1. TENANTS TABLE (Customer Organizations)
-- ============================================
CREATE OR REPLACE TABLE dnb_hackathon_west_2.data_wolves.tenants (
    tenant_id STRING PRIMARY KEY,
    tenant_name STRING NOT NULL,
    industry STRING,
    company_size STRING,  -- SMALL, MEDIUM, LARGE, ENTERPRISE
    service_tier STRING,  -- BASIC, PROFESSIONAL, ENTERPRISE
    security_score INT,  -- 1-100 overall security posture
    risk_level STRING,  -- LOW, MEDIUM, HIGH, CRITICAL
    status STRING,  -- ACTIVE, SUSPENDED, TRIAL
    created_at TIMESTAMP
);


-- ============================================
-- 2. ALERTS TABLE (Security Alerts)
-- ============================================
CREATE OR REPLACE TABLE dnb_hackathon_west_2.data_wolves.alerts (
    alert_id STRING PRIMARY KEY,
    tenant_id STRING NOT NULL,
    alert_name STRING NOT NULL,
    alert_type STRING,  -- MALWARE, PHISHING, INTRUSION, DATA_EXFILTRATION, POLICY_VIOLATION, SUSPICIOUS_ACTIVITY
    severity STRING,  -- LOW, MEDIUM, HIGH, CRITICAL
    status STRING,  -- OPEN, IN_PROGRESS, RESOLVED, FALSE_POSITIVE, CLOSED
    detection_time TIMESTAMP NOT NULL,
    assigned_to STRING,
    affected_assets STRING,
    description STRING,
    CONSTRAINT fk_alert_tenant FOREIGN KEY (tenant_id) REFERENCES dnb_hackathon_west_2.data_wolves.tenants(tenant_id)
);

