CREATE OR REPLACE TABLE dnb_hackathon_west_2.data_wolves.events (
    event_id STRING PRIMARY KEY,
    tenant_id STRING NOT NULL,
    alert_id STRING,  -- Nullable
    event_type STRING,  -- LOGIN, LOGOUT, FILE_ACCESS, NETWORK_CONNECTION, PROCESS_EXECUTION
    severity STRING,  -- INFO, LOW, MEDIUM, HIGH, CRITICAL
