-- VIEWS FOR ANALYSIS
-- ============================================

-- Alert summary by tenant
CREATE OR REPLACE VIEW dnb_hackathon_west_2.data_wolves.v_alert_summary AS
SELECT 
    t.tenant_id,
    t.tenant_name,
    t.service_tier,
    t.risk_level,
    COUNT(a.alert_id) as total_alerts,
    SUM(CASE WHEN a.severity = 'CRITICAL' THEN 1 ELSE 0 END) as critical_alerts,
    SUM(CASE WHEN a.severity = 'HIGH' THEN 1 ELSE 0 END) as high_alerts,
    SUM(CASE WHEN a.severity = 'MEDIUM' THEN 1 ELSE 0 END) as medium_alerts,
    SUM(CASE WHEN a.severity = 'LOW' THEN 1 ELSE 0 END) as low_alerts,
    SUM(CASE WHEN a.status = 'OPEN' THEN 1 ELSE 0 END) as open_alerts,
    SUM(CASE WHEN a.status = 'RESOLVED' THEN 1 ELSE 0 END) as resolved_alerts,
    AVG(a.priority_score) as avg_priority_score
FROM dnb_hackathon_west_2.data_wolves.tenants t
LEFT JOIN dnb_hackathon_west_2.data_wolves.alerts a ON t.tenant_id = a.tenant_id
GROUP BY t.tenant_id, t.tenant_name, t.service_tier, t.risk_level;

-- Event summary by category
CREATE OR REPLACE VIEW dnb_hackathon_west_2.data_wolves.v_event_summary AS
SELECT 
    e.tenant_id,
    t.tenant_name,
    e.event_category,
    e.severity,
    COUNT(*) as event_count,
    MIN(e.event_timestamp) as first_event,
    MAX(e.event_timestamp) as last_event
FROM dnb_hackathon_west_2.data_wolves.events e
JOIN dnb_hackathon_west_2.data_wolves.tenants t ON e.tenant_id = t.tenant_id
GROUP BY e.tenant_id, t.tenant_name, e.event_category, e.severity;

-- SOC analyst performance and customer interactions
CREATE OR REPLACE VIEW dnb_hackathon_west_2.data_wolves.v_analyst_performance AS
SELECT 
    t.user_name,
    COUNT(DISTINCT t.alert_id) as alerts_handled,
    SUM(CASE WHEN t.activity_type = 'CLOSED' THEN 1 ELSE 0 END) as alerts_resolved,
    SUM(CASE WHEN t.is_customer_facing = TRUE THEN 1 ELSE 0 END) as customer_interactions
FROM dnb_hackathon_west_2.data_wolves.support_tickets t
GROUP BY t.user_name;
